templates.template:
:${localname} a rdf:Property, owl:DatatypeProperty ;
	rdfs:label "${label}" ;
	rdfs:comment "${comment}" ;
	rdfs:domain ${domain} ;
	rdfs:range rdfs:Literal .

${cursor}

:${localname} a rdf:Property, owl:ObjectProperty ;
	rdfs:label "${label}" ;
	rdfs:comment "${comment}" ;
	rdfs:domain ${domain} ;
	rdfs:range ${range} .

${cursor}

@prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#>.
@prefix owl: <http://www.w3.org/2002/07/owl#>.
@prefix xsd: <http://www.w3.org/2001/XMLSchema#>.
@prefix dcterms: <http://purl.org/dc/terms/>.
@prefix ${namespacePrefix}: <http://${namespaceUri}>.

${namespacePrefix}: a owl:Ontology;
    rdfs:label "${label}";
    dcterms:description "${description}";
    dcterms:creator "${creator}";
    <http://purl.org/vocab/vann/preferredNamespaceUri> "http://${namespaceUri}"^^xsd:string;
    <http://purl.org/vocab/vann/preferredNamespacePrefix> "${namespacePrefix}"^^xsd:string;
    owl:versionInfo "${versionInfo}" .

${cursor}
